mapper.select:
SELECT COUNT(*)
FROM borrow_record
WHERE user_id = #{userId}
  AND book_id = #{bookId}
  AND status = 0
  AND deleted = 0

SELECT br.*, u.username, u.real_name as user_real_name, b.title as book_name, b.isbn as book_isbn, CASE br.status WHEN 0 THEN '借阅中' WHEN 1 THEN '已归还' WHEN 2 THEN '逾期未还' WHEN 3 THEN '逾期已还' END as status_desc FROM borrow_record br LEFT JOIN sys_user u ON br.user_id = u.id LEFT JOIN book_info b ON br.book_id = b.id
AND ( u.username LIKE CONCAT('%', #{query.keyword}, '%') OR b.title LIKE CONCAT('%', #{query.keyword}, '%') OR b.isbn LIKE CONCAT('%', #{query.keyword}, '%') )
AND br.user_id = #{query.userId}
AND br.book_id = #{query.bookId}
AND br.status = #{query.status}
AND br.create_time >= #{query.startTime}
AND br.create_time <= #{query.endTime}
AND br.deleted = 0
ORDER BY br.create_time DESC

SELECT COUNT(*) as totalBorrowCount, SUM(CASE WHEN status = 0 THEN 1 ELSE 0 END) as currentBorrowCount, SUM(CASE WHEN status = 1 THEN 1 ELSE 0 END) as returnedCount, SUM(CASE WHEN status = 2 THEN 1 ELSE 0 END) as overdueCount, SUM(CASE WHEN status = 3 THEN 1 ELSE 0 END) as overdueReturnedCount, COALESCE(SUM(fine_amount), 0) as totalFineAmount, COALESCE(SUM(CASE WHEN fine_paid = 1 THEN fine_amount ELSE 0 END), 0) as paidFineAmount, COALESCE(SUM(CASE WHEN fine_paid = 0 THEN fine_amount ELSE 0 END), 0) as unpaidFineAmount FROM borrow_record
AND user_id = #{userId}
AND deleted = 0
